function [sosData] = MRSISumOfSquares(inputData, channelList)  sosChannelList = [];  if(channelList == -1)    nChannels = size(inputData, 4);    sosChannelList = 1:nChannels;  else    nChannels = length(channelList);      sosChannelList = channelList;  end    sosData = zeros([size(inputData, 1), size(inputData, 2), size(inputData, 3)]);  for jj = 1:nChannels    thisCoil = sosChannelList(jj);    sosData = sosData + inputData(:,:,:,thisCoil) .* inputData(:,:,:,thisCoil);  end  sosData = sqrt(sosData);end