function [output] = betaOfN(N)  twoNMinusOneDF = 0;  if(N == 1 || N == 0)    twoNMinusOneDF = 1;  elseif(mod(N, 2) == 0)% N even, 2N-1 odd       k = round(N);    twoNMinusOneDF = factorial(2*k) / (factorial(k) * 2^k);  else% N odd, 2N-1 even     twoNMinusOne = 2*N - 1;    k = round(twoNMinusOne/2);    twoNMinusOneDF =  factorial(k) * 2^k;  end    output = sqrt(pi/2) * twoNMinusOneDF / ( factorial(N-1) * 2^(N-1) );  end